import Radio from '@/components/radio/Radio'
import RadioButton from '@/components/radio/RadioButton'

import { use, registerComponent } from '@/utils/plugins'

const Plugin = {
    install(Vue) {
        registerComponent(Vue, Radio)
        registerComponent(Vue, RadioButton)
    }
}

use(Plugin)

export default Plugin

export {
    Radio as BRadio,
    RadioButton as BRadioButton
}

export default {
    props: {
        value: [String, Number, Boolean, Function, Object, Array],
        nativeValue: [String, Number, Boolean, Function, Object, Array],
        type: String,
        disabled: Boolean,
        required: Boolean,
        name: String,
        size: String
    },
    data() {
        return {
            newValue: this.value
        }
    },
    computed: {
        computedValue: {
            get() {
                return this.newValue
            },
            set(value) {
                this.newValue = value
                this.$emit('input', value)
            }
        }
    },
    watch: {
        /**
        * When v-model change, set internal value.
        */
        value(value) {
            this.newValue = value
        }
    },
    methods: {
        focus() {
            // MacOS FireFox and Safari do not focus when clicked
            this.$refs.input.focus()
        }
    }
}

export const use = (plugin) => {
    if (typeof window !== 'undefined' && window.Vue) {
        window.Vue.use(plugin)
    }
}

export const registerComponent = (Vue, component) => {
    Vue.component(component.name, component)
}

export const registerComponentProgrammatic = (Vue, property, component) => {
    if (!Vue.prototype.$buefy) Vue.prototype.$buefy = {}
    Vue.prototype.$buefy[property] = component
}
