import { initAccordions } from '@/components/accordion';
import { initCarousels } from '@/components/carousel';
import { initCopyClipboards } from '@/components/clipboard';
import { initCollapses } from '@/components/collapse';
import { initDials } from '@/components/dial';
import { initDismisses } from '@/components/dismiss';
import { initDrawers } from '@/components/drawer';
import { initDropdowns } from '@/components/dropdown';
import { initInputCounters } from '@/components/input-counter';
import { initModals } from '@/components/modal';
import { initPopovers } from '@/components/popover';
import { initTabs } from '@/components/tabs';
import { initTooltips } from '@/components/tooltip';
import { initDatepickers } from '@/components/datepicker';

export function initFlowbite() {
    initAccordions();
    initCollapses();
    initCarousels();
    initDismisses();
    initDropdowns();
    initModals();
    initDrawers();
    initTabs();
    initTooltips();
    initPopovers();
    initDials();
    initInputCounters();
    initCopyClipboards();
    initDatepickers();
}

if (typeof window !== 'undefined') {
    window.initFlowbite = initFlowbite;
}
