import { MessageCircle, Sparkles } from "lucide-react";
import { SettingsGeneration } from "@/components/kokonutui/blocks/ai-card-generation/settings-generation";
import { Input } from "@/components/ui/input";

interface VideoSettings {
    style: string;
    backgroundColor: string;
    lighting: string;
    pose: string;
    aspectRatio: string;
}

interface FormProps {
    onSubmit: (e: React.FormEvent) => void;
    settings: VideoSettings;
    onSettingsChange: (settings: VideoSettings) => void;
}

export const FormGeneration = ({
    onSubmit,
    settings,
    onSettingsChange,
}: FormProps) => {
    return (
        <form
            onSubmit={onSubmit}
            className="flex flex-col gap-2 flex-1 p-4 justify-between"
        >
            <div className="space-y-2">
                <div className="flex items-center gap-2">
                    <MessageCircle className="w-4 h-4 text-zinc-500" />
                    <span className="text-sm text-zinc-500">Prompt</span>
                </div>
                <Input
                    type="text"
                    size={16}
                    defaultValue="Picture of a women with orange background"
                    placeholder="Describe the portrait you want to create..."
                    className="w-full bg-zinc-100 dark:bg-zinc-800 text-sm text-zinc-900 dark:text-zinc-100 placeholder:text-zinc-500 rounded-xl focus:outline-hidden focus-visible:ring-offset-0 focus-visible:ring-0 focus-visible:border-zinc-900 dark:focus-visible:border-zinc-100"
                />
            </div>

            <SettingsGeneration
                settings={settings}
                onSettingsChange={onSettingsChange}
            />

            <button
                type="submit"
                className="w-full h-9 flex items-center justify-center gap-2 bg-zinc-900 hover:bg-zinc-800 dark:bg-white dark:hover:bg-zinc-100 dark:text-zinc-900 text-white text-sm font-medium rounded-xl transition-colors self-end"
            >
                <Sparkles className="w-4 h-4" />
                Generate Portrait
            </button>
        </form>
    );
};

import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
    return twMerge(clsx(inputs));
}

import * as React from "react"

import { cn } from "@/lib/utils"

export interface InputProps
  extends React.InputHTMLAttributes<HTMLInputElement> {}

const Input = React.forwardRef<HTMLInputElement, InputProps>(
  ({ className, type, ...props }, ref) => {
    return (
      <input
        type={type}
        className={cn(
          "flex h-10 w-full rounded-md border border-zinc-200 bg-white px-3 py-2 text-base ring-offset-white file:border-0 file:bg-transparent file:text-sm file:font-medium file:text-zinc-950 placeholder:text-zinc-500 focus-visible:outline-hidden focus-visible:ring-2 focus-visible:ring-zinc-950 focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 md:text-sm dark:border-zinc-800 dark:bg-zinc-950 dark:ring-offset-zinc-950 dark:file:text-zinc-50 dark:placeholder:text-zinc-400 dark:focus-visible:ring-zinc-300",
          className
        )}
        ref={ref}
        {...props}
      />
    )
  }
)
Input.displayName = "Input"

export { Input }
