"use client";

import { cn } from "@/lib/utils";
import { motion, useAnimation } from "motion/react";
import { Zap } from "lucide-react";
import { useState } from "react";
import { Button } from "@/components/ui/button";

interface Btn04Props extends React.ButtonHTMLAttributes<HTMLButtonElement> {
    onPowerUp?: () => void;
    chargeDuration?: number;
}

export default function Btn04({
    className,
    onPowerUp,
    chargeDuration = 2000,
    ...props
}: Btn04Props) {
    const [isCharging, setIsCharging] = useState(false);
    const particleControls = useAnimation();
    const chargeControls = useAnimation();

    async function handleStart() {
        setIsCharging(true);
        chargeControls.set({ height: "100%", y: "100%" });
        await chargeControls.start({
            y: "0%",
            transition: {
                duration: chargeDuration / 1000,
                ease: "easeOut",
            },
        });

        await particleControls.start({
            scale: [1, 1.5],
            opacity: [1, 0],
            transition: { duration: 0.3 },
        });

        onPowerUp?.();
    }

    function handleEnd() {
        setIsCharging(false);
        chargeControls.stop();
        chargeControls.start({
            y: "100%",
            transition: { duration: 0.2 },
        });
    }

    return (
        <Button
            className={cn(
                "min-w-[120px] sm:min-w-40 relative overflow-hidden",
                "bg-indigo-100 dark:bg-indigo-900",
                "hover:bg-indigo-200 dark:hover:bg-indigo-800",
                "text-indigo-900 dark:text-indigo-300",
                "border border-indigo-300 dark:border-indigo-700",
                "transition-colors duration-300",
                "touch-none",
                className
            )}
            onMouseDown={handleStart}
            onMouseUp={handleEnd}
            onMouseLeave={handleEnd}
            onTouchStart={handleStart}
            onTouchEnd={handleEnd}
            onTouchCancel={handleEnd}
            {...props}
        >
            <motion.div
                initial={{ height: "100%", y: "100%" }}
                animate={chargeControls}
                className={cn(
                    "absolute left-0 bottom-0 w-full",
                    "bg-linear-to-t from-indigo-400 to-indigo-300",
                    "dark:from-indigo-500 dark:to-indigo-400",
                    "opacity-50"
                )}
            />
            <motion.div
                initial={{ scale: 0, opacity: 0 }}
                animate={particleControls}
                className={cn(
                    "absolute inset-0",
                    "bg-indigo-200 dark:bg-indigo-400",
                    "rounded-full"
                )}
            />
            <span className="relative z-10 w-full flex items-center justify-center gap-2">
                <Zap
                    className={cn(
                        "w-4 h-4 transition-transform",
                        isCharging && "animate-bounce"
                    )}
                />
                {!isCharging ? "Power Up!" : "Charging..."}
            </span>
        </Button>
    );
}

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
    "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium ring-offset-white transition-colors focus-visible:outline-hidden focus-visible:ring-2 focus-visible:ring-zinc-950 focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0 dark:ring-offset-zinc-950 dark:focus-visible:ring-zinc-300 cursor-pointer",
    {
        variants: {
            variant: {
                default:
                    "bg-zinc-900 text-zinc-50 hover:bg-zinc-900/90 dark:bg-zinc-50 dark:text-zinc-900 dark:hover:bg-zinc-50/90",
                destructive:
                    "bg-red-500 text-zinc-50 hover:bg-red-500/90 dark:bg-red-900 dark:text-zinc-50 dark:hover:bg-red-900/90",
                outline:
                    "border border-zinc-200 bg-white hover:bg-zinc-100 hover:text-zinc-900 dark:border-zinc-800 dark:bg-zinc-950 dark:hover:bg-zinc-800 dark:hover:text-zinc-50",
                secondary:
                    "bg-zinc-100 text-zinc-900 hover:bg-zinc-100/80 dark:bg-zinc-800 dark:text-zinc-50 dark:hover:bg-zinc-800/80",
                ghost: "hover:bg-zinc-100 hover:text-zinc-900 dark:hover:bg-zinc-800 dark:hover:text-zinc-50",
                link: "text-zinc-900 underline-offset-4 hover:underline dark:text-zinc-50",
            },
            size: {
                default: "h-10 px-4 py-2",
                sm: "h-9 rounded-md px-3",
                lg: "h-11 rounded-md px-8",
                icon: "h-10 w-10",
            },
        },
        defaultVariants: {
            variant: "default",
            size: "default",
        },
    }
);

export interface ButtonProps
    extends React.ButtonHTMLAttributes<HTMLButtonElement>,
        VariantProps<typeof buttonVariants> {
    asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
    ({ className, variant, size, asChild = false, ...props }, ref) => {
        const Comp = asChild ? Slot : "button";
        return (
            <Comp
                className={cn(buttonVariants({ variant, size, className }))}
                ref={ref}
                {...props}
            />
        );
    }
);
Button.displayName = "Button";

export { Button, buttonVariants };
