"use client";

import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { Sun, Moon, Laptop } from "lucide-react";
import { useState } from "react";

type ThemeMode = "light" | "dark" | "system";

interface Btn10Props extends React.ButtonHTMLAttributes<HTMLButtonElement> {
    variant?: "pill" | "minimal" | "ghost";
    size?: "sm" | "default" | "lg";
    showLabel?: boolean;
    defaultMode?: ThemeMode;
    onModeChange?: (mode: ThemeMode) => void;
}

export default function Btn10({
    className,
    variant = "minimal",
    size = "default",
    showLabel = true,
    defaultMode = "system",
    onModeChange,
    ...props
}: Btn10Props) {
    const [mode, setMode] = useState<ThemeMode>(defaultMode);

    const handleClick = () => {
        const modes: ThemeMode[] = ["light", "dark", "system"];
        const currentIndex = modes.indexOf(mode);
        const nextMode = modes[(currentIndex + 1) % modes.length];
        setMode(nextMode);
        onModeChange?.(nextMode);
    };

    const getIcon = () => {
        switch (mode) {
            case "light":
                return Sun;
            case "dark":
                return Moon;
            case "system":
                return Laptop;
        }
    };

    const Icon = getIcon();

    const variants = {
        pill: [
            "rounded-full",
            "bg-linear-to-b from-white to-zinc-50",
            "dark:from-zinc-900 dark:to-zinc-800",
            "border border-zinc-200 dark:border-zinc-800",
            "hover:border-zinc-300 dark:hover:border-zinc-700",
            "shadow-xs",
        ],
        minimal: [
            "rounded-lg",
            "bg-zinc-100 dark:bg-zinc-800",
            "hover:bg-zinc-200 dark:hover:bg-zinc-700",
        ],
        ghost: [
            "rounded-lg",
            "hover:bg-zinc-100 dark:hover:bg-zinc-800",
            "border border-zinc-200 dark:border-zinc-800",
        ],
    };

    const sizes = {
        sm: "h-8 px-3 text-sm",
        default: "h-10 px-4",
        lg: "h-11 px-5",
    };

    return (
        <Button
            className={cn(
                "relative group transition-all duration-200",
                "text-zinc-600 dark:text-zinc-300",
                variants[variant],
                sizes[size],
                className
            )}
            onClick={handleClick}
            {...props}
        >
            <div
                className={cn(
                    "flex items-center gap-2",
                    "transition-all duration-200"
                )}
            >
                <Icon
                    className={cn(
                        "transition-all duration-200",
                        size === "sm" && "w-3.5 h-3.5",
                        size === "default" && "w-4 h-4",
                        size === "lg" && "w-5 h-5",
                        "group-hover:rotate-[-8deg] group-hover:scale-110",
                        "group-active:scale-95"
                    )}
                />
                {showLabel && <span className="capitalize">{mode}</span>}
            </div>
            <span
                className={cn(
                    "absolute inset-0",
                    "bg-linear-to-r from-zinc-500/0 via-zinc-500/10 to-zinc-500/0",
                    "translate-x-[-50%]",
                    "group-hover:translate-x-[50%]",
                    "transition-transform duration-500",
                    "ease-out"
                )}
            />
        </Button>
    );
}

import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
    return twMerge(clsx(inputs));
}

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
    "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium ring-offset-white transition-colors focus-visible:outline-hidden focus-visible:ring-2 focus-visible:ring-zinc-950 focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0 dark:ring-offset-zinc-950 dark:focus-visible:ring-zinc-300 cursor-pointer",
    {
        variants: {
            variant: {
                default:
                    "bg-zinc-900 text-zinc-50 hover:bg-zinc-900/90 dark:bg-zinc-50 dark:text-zinc-900 dark:hover:bg-zinc-50/90",
                destructive:
                    "bg-red-500 text-zinc-50 hover:bg-red-500/90 dark:bg-red-900 dark:text-zinc-50 dark:hover:bg-red-900/90",
                outline:
                    "border border-zinc-200 bg-white hover:bg-zinc-100 hover:text-zinc-900 dark:border-zinc-800 dark:bg-zinc-950 dark:hover:bg-zinc-800 dark:hover:text-zinc-50",
                secondary:
                    "bg-zinc-100 text-zinc-900 hover:bg-zinc-100/80 dark:bg-zinc-800 dark:text-zinc-50 dark:hover:bg-zinc-800/80",
                ghost: "hover:bg-zinc-100 hover:text-zinc-900 dark:hover:bg-zinc-800 dark:hover:text-zinc-50",
                link: "text-zinc-900 underline-offset-4 hover:underline dark:text-zinc-50",
            },
            size: {
                default: "h-10 px-4 py-2",
                sm: "h-9 rounded-md px-3",
                lg: "h-11 rounded-md px-8",
                icon: "h-10 w-10",
            },
        },
        defaultVariants: {
            variant: "default",
            size: "default",
        },
    }
);

export interface ButtonProps
    extends React.ButtonHTMLAttributes<HTMLButtonElement>,
        VariantProps<typeof buttonVariants> {
    asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
    ({ className, variant, size, asChild = false, ...props }, ref) => {
        const Comp = asChild ? Slot : "button";
        return (
            <Comp
                className={cn(buttonVariants({ variant, size, className }))}
                ref={ref}
                {...props}
            />
        );
    }
);
Button.displayName = "Button";

export { Button, buttonVariants };
