"use client";

import * as React from "react";
import { motion } from "motion/react";
import { cn } from "@/lib/utils";
import { CheckCheck } from "lucide-react";

interface PackageManagerTabsProps {
    onSelect: (packageManager: string) => void;
    commandName: string;
    prePath: string;
}

const packageManagers = [
    { id: "npm", title: "npm" },
    { id: "bun", title: "bun" },
    { id: "pnpm", title: "pnpm" },
];

export function PackageManagerTabs({
    onSelect,
    commandName,
    prePath,
}: PackageManagerTabsProps) {
    const [selected, setSelected] = React.useState<string>("bun");
    const [dimensions, setDimensions] = React.useState({ width: 0, left: 0 });
    const [isCopied, setIsCopied] = React.useState(false);

    // Reference for the selected button
    const buttonRefs = React.useRef<Map<string, HTMLButtonElement>>(new Map());
    const containerRef = React.useRef<HTMLDivElement>(null);
    const copyButtonRef = React.useRef<HTMLButtonElement>(null);

    // Update dimensions whenever selected tab changes or on mount
    React.useLayoutEffect(() => {
        const updateDimensions = () => {
            const selectedButton = buttonRefs.current.get(selected);
            const container = containerRef.current;

            if (selectedButton && container) {
                const rect = selectedButton.getBoundingClientRect();
                const containerRect = container.getBoundingClientRect();

                setDimensions({
                    width: rect.width,
                    left: rect.left - containerRect.left,
                });
            }
        };

        // Initial update
        requestAnimationFrame(() => {
            updateDimensions();
        });

        // Update on resize
        window.addEventListener("resize", updateDimensions);
        return () => window.removeEventListener("resize", updateDimensions);
    }, [selected]);

    const handleTabClick = (tabId: string) => {
        setSelected(tabId);
        onSelect(tabId);
    };

    const handleKeyDown = (
        e: React.KeyboardEvent<HTMLButtonElement>,
        tabId: string
    ) => {
        if (e.key === "Enter" || e.key === " ") {
            e.preventDefault();
            handleTabClick(tabId);
        }
    };

    const getCommand = (packageManager: string): string => {
        if (packageManager === "pnpm") {
            return `pnpm dlx shadcn@latest add ${prePath}/${commandName}`;
        } else if (packageManager === "npm") {
            return `npx shadcn@latest add ${prePath}/${commandName}`;
        } else {
            return `bunx --bun shadcn@latest add ${prePath}/${commandName}`;
        }
    };

    const handleCopyCommand = () => {
        const commandToCopy = getCommand(selected);
        navigator.clipboard.writeText(commandToCopy);
        setIsCopied(true);
        setTimeout(() => {
            setIsCopied(false);
        }, 1000);
    };

    return (
        <div className="flex flex-col sm:flex-row items-stretch sm:items-center gap-1 sm:gap-2">
            <div
                ref={containerRef}
                role="tablist"
                aria-label="Package manager tabs"
                className={cn(
                    "relative flex items-center justify-start gap-1",
                    "bg-background rounded-sm",
                    "border",
                    "transition-all duration-200",
                    "h-7 text-black dark:text-white"
                )}
            >
                <motion.div
                    className="absolute rounded-sm z-[1] bg-black dark:bg-white"
                    initial={false}
                    animate={{
                        width: dimensions.width - 4,
                        x: dimensions.left + 2,
                        opacity: 1,
                    }}
                    transition={{
                        type: "spring",
                        stiffness: 400,
                        damping: 30,
                    }}
                    style={{ height: "calc(100% - 4px)", top: "2px" }}
                />

                <div className="flex relative z-[2]">
                    {packageManagers.map((item) => {
                        const isSelected = selected === item.id;
                        return (
                            <motion.button
                                key={item.id}
                                ref={(el) => {
                                    if (el) buttonRefs.current.set(item.id, el);
                                    else buttonRefs.current.delete(item.id);
                                }}
                                type="button"
                                role="tab"
                                aria-selected={isSelected}
                                aria-controls={`panel-${item.id}`}
                                id={`tab-${item.id}`}
                                tabIndex={isSelected ? 0 : -1}
                                onClick={() => handleTabClick(item.id)}
                                onKeyDown={(e) => handleKeyDown(e, item.id)}
                                className={cn(
                                    "relative flex items-center justify-center gap-0.5 rounded-sm px-2 py-0.5 h-7",
                                    "text-xs font-medium transition-all duration-300",
                                    "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring",
                                    "truncate",
                                    isSelected
                                        ? "text-white dark:text-black"
                                        : " hover:bg-muted/50 hover:text-foreground text-black/50 dark:text-white/50"
                                )}
                            >
                                <span className="truncate">{item.title}</span>
                            </motion.button>
                        );
                    })}
                </div>
            </div>

            <button
                ref={copyButtonRef}
                type="button"
                onClick={handleCopyCommand}
                className={cn(
                    "h-7 text-xs font-medium",
                    "border border-black/10 dark:border-white/10",
                    "rounded-sm px-2",
                    "dark:text-white text-black",
                    "transition-all duration-200",
                    "group flex items-center justify-center gap-1",
                    "w-full",
                    "min-w-[250px]",
                    "relative"
                )}
            >
                <span
                    className={cn(
                        "absolute inset-0 flex items-center justify-center gap-1",
                        "transition-opacity duration-200",
                        isCopied ? "opacity-100" : "opacity-0"
                    )}
                >
                    <CheckCheck className="h-3.5 w-3.5 text-green-500 dark:text-green-500" />
                    <span className="truncate text-black/50 dark:text-white/50">
                        Copied to clipboard
                    </span>
                </span>
                <span
                    className={cn(
                        "truncate text-black/50 dark:text-white/50",
                        "transition-opacity duration-200",
                        isCopied ? "opacity-0" : "opacity-100"
                    )}
                >
                    {getCommand(selected)}
                </span>
            </button>
        </div>
    );
}

"use client";

import {
    useActionState,
    useEffect,
    useState,
    useTransition,
    useRef,
    type RefObject,
} from "react";
import { Code } from "lucide-react";
import { copyComponent } from "@/lib/action";
import { cn } from "@/lib/utils";
import { OpenInV0Button } from "@/components/open-in-v0-button";
import { AnimatePresence, motion } from "motion/react";
import { PackageManagerTabs } from "@/components/mdx/package-manager-tabs";
import { CheckCheck } from "lucide-react";

function SuccessParticles({
    buttonRef,
}: {
    buttonRef: React.RefObject<HTMLButtonElement>;
}) {
    const rect = buttonRef.current?.getBoundingClientRect();
    if (!rect) return null;

    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    // Create a unique key for each particle to satisfy the linter
    const particles = Array.from({ length: 6 }, (_, index) => ({
        id: `particle-${index}-${Math.random().toString(36).substr(2, 9)}`,
        index, // Pass index for staggering delay
    }));

    return (
        <AnimatePresence>
            {particles.map((particle) => (
                <motion.div
                    key={particle.id}
                    className="fixed w-1 h-1 bg-black dark:bg-white rounded-full"
                    style={{ left: centerX, top: centerY }}
                    initial={{
                        scale: 0,
                        x: 0,
                        y: 0,
                    }}
                    animate={{
                        scale: [0, 1, 0],
                        x: [
                            0,
                            (particle.index % 2 ? 1 : -1) *
                                (Math.random() * 50 + 20),
                        ],
                        y: [0, -Math.random() * 50 - 20],
                    }}
                    transition={{
                        duration: 0.6,
                        delay: particle.index * 0.1, // Use particle.index for delay
                        ease: "easeOut",
                    }}
                />
            ))}
        </AnimatePresence>
    );
}

export default function PreviewContent({
    link,
    prePath,
    isBlock = false,
}: {
    link: string;
    prePath: string;
    isBlock?: boolean;
}) {
    const [isPending, startTransition] = useTransition();
    const [state, formAction] = useActionState(copyComponent, {
        error: "",
        content: "",
        success: false,
    });
    const [showLoginDialog, setShowLoginDialog] = useState(false);
    const [isCopied, setIsCopied] = useState(false);
    const [isHovered, setIsHovered] = useState(false);
    const [isTerminalCopied, setIsTerminalCopied] = useState(false);

    const handleCopyClick = async () => {
        const [folder, filename] = link.split("/");

        startTransition(async () => {
            const formData = new FormData();
            formData.append("folder", folder);
            formData.append("fileName", filename);

            formAction(formData);
        });
    };

    const getFileName = () => {
        const [folder, filename] = link.split("/");
        return filename ? filename : folder;
    };

    const handleTerminalClick = (packageManager: string) => {
        const [folder, filename] = link.split("/");
        const componentName = filename ? filename : folder;

        let commandToCopy: string;
        const componentAddCommand = `shadcn@latest add ${prePath}/${componentName}`;

        if (packageManager === "pnpm") {
            commandToCopy = `pnpm dlx ${componentAddCommand}`;
        } else if (packageManager === "npm") {
            commandToCopy = `npx ${componentAddCommand}`;
        } else {
            commandToCopy = `bunx --bun ${componentAddCommand}`;
        }

        navigator.clipboard.writeText(commandToCopy);
        setIsTerminalCopied(true);
        setTimeout(() => {
            setIsTerminalCopied(false);
        }, 1000);
    };

    const openInV0 = () => {
        const [folder, filename] = link.split("/");

        return filename ? filename : folder;
    };

    useEffect(() => {
        if (state.error) {
            setShowLoginDialog(true);
        }
        if (state.success && state.content) {
            setIsCopied(true);
            navigator.clipboard.writeText(state.content);

            setTimeout(() => {
                setIsCopied(false);
            }, 2000);
        }
    }, [state]);

    const terminalButtonRef = useRef<HTMLButtonElement>(null);
    const copyButtonRef = useRef<HTMLButtonElement>(null);

    return (
        <>
            {isTerminalCopied && (
                <SuccessParticles
                    buttonRef={
                        terminalButtonRef as RefObject<HTMLButtonElement>
                    }
                />
            )}
            {isCopied && (
                <SuccessParticles
                    buttonRef={copyButtonRef as RefObject<HTMLButtonElement>}
                />
            )}
            <div className="relative flex flex-col sm:flex-row items-start sm:items-center justify-between w-full gap-1 sm:gap-2">
                <div className="w-full sm:w-auto">
                    <PackageManagerTabs
                        onSelect={handleTerminalClick}
                        commandName={getFileName()}
                        prePath={prePath}
                    />
                </div>
                <div className="flex items-center gap-2 w-full sm:w-auto justify-end mt-1 sm:mt-0">
                    <OpenInV0Button name={openInV0()} />

                    {!isBlock && (
                        <form
                            onSubmit={(e) => {
                                e.preventDefault();
                                handleCopyClick();
                            }}
                            className="w-full sm:w-auto"
                        >
                            <button
                                ref={copyButtonRef}
                                type="submit"
                                disabled={isPending}
                                className={cn(
                                    "relative overflow-hidden",
                                    "h-7 px-2 text-xs font-medium",
                                    "bg-black dark:bg-white",
                                    "text-white dark:text-black",
                                    "hover:bg-black/90 dark:hover:bg-white/90",
                                    "hover:text-white dark:hover:text-black",
                                    "transition-all duration-200",
                                    "group flex items-center justify-center gap-1",
                                    "rounded-sm",
                                    "shadow-none py-0 my-0",
                                    "w-fit md:w-full"
                                )}
                            >
                                {isCopied ? (
                                    <CheckCheck className="h-3.5 w-3.5 text-white dark:text-black" />
                                ) : (
                                    <Code
                                        className={cn(
                                            "h-3.5 w-3.5",
                                            "transition-all duration-200",
                                            "group-hover:rotate-12"
                                        )}
                                    />
                                )}
                                <span>Copy</span>
                            </button>
                        </form>
                    )}
                </div>
            </div>
        </>
    );
}

import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

const prePath = process.env.VERCEL_PROJECT_PRODUCTION_URL
    ? `https://${process.env.VERCEL_PROJECT_PRODUCTION_URL}`
    : `https://${process.env.NEXT_PUBLIC_SITE_URL}`;

export function OpenInV0Button({
    name,
    className,
}: { name: string } & React.ComponentProps<typeof Button>) {
    return (
        <Button
            aria-label="Open in v0"
            className={cn(
                "h-7 gap-1 rounded-sm shadow-none bg-black px-2 text-xs text-white hover:bg-zinc-800 dark:bg-white dark:text-black dark:hover:bg-zinc-200 transition-colors duration-200 not-prose",
                className
            )}
            asChild
        >
