                className
            )}
            asChild
        >
            <a
                href={`https://v0.dev/chat/api/open?url=${prePath}/r/${name}.json`}
                target="_blank"
                rel="noreferrer"
            >
                Open in{" "}
                <svg
                    viewBox="0 0 40 20"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                    className="h-5 w-5 text-current"
                >
                    <path
                        d="M23.3919 0H32.9188C36.7819 0 39.9136 3.13165 39.9136 6.99475V16.0805H36.0006V6.99475C36.0006 6.90167 35.9969 6.80925 35.9898 6.71766L26.4628 16.079C26.4949 16.08 26.5272 16.0805 26.5595 16.0805H36.0006V19.7762H26.5595C22.6964 19.7762 19.4788 16.6139 19.4788 12.7508V3.68923H23.3919V12.7508C23.3919 12.9253 23.4054 13.0977 23.4316 13.2668L33.1682 3.6995C33.0861 3.6927 33.003 3.68923 32.9188 3.68923H23.3919V0Z"
                        fill="currentColor"
                    ></path>
                    <path
                        d="M13.7688 19.0956L0 3.68759H5.53933L13.6231 12.7337V3.68759H17.7535V17.5746C17.7535 19.6705 15.1654 20.6584 13.7688 19.0956Z"
                        fill="currentColor"
                    ></path>
                </svg>
            </a>
        </Button>
    );
}

"use server";

import path from "path";
import { promises as fs } from "fs";
import { cache } from "react";

// Create a cached version of the file reading operation
const readFileCache = cache(async (filePath: string) => {
    return await fs.readFile(filePath, "utf-8");
});

// Improve caching for the entire component getter
export const getComponent = async (fileName: string | null, folder: string) => {
    const baseDir = path.join(process.cwd(), "components/kokonutui");
    if (!fileName || fileName === "undefined") {
        const fullPath = path.join(baseDir, `${folder}.tsx`);
        // console.log("fullPath", fullPath);
        return await readFileCache(fullPath);
    }

    const fullPath = path.join(baseDir, folder, `${fileName}.tsx`);

    return await readFileCache(fullPath);
};

export type CopyComponentState = {
    error: string;
    content: string;
    success: boolean;
};

export const copyComponent = async (
    prevState: CopyComponentState,
    formData: FormData
) => {
    try {
        const folder = formData.get("folder");
        const fileName = formData.get("fileName");

        if (!folder && !fileName) {
            return {
                error: "Folder or file name not found",
                content: "",
                success: false,
            };
        }

        const content = await getComponent(
            fileName as string,
            folder as string
        );

        if (!content) {
            return {
                error: "Component not found",
                content: "",
                success: false,
            };
        }

        return {
            error: "",
            content: content,
            success: true,
        };
    } catch (error) {
        console.error(error);
        return {
            error: "Failed to copy component",
            content: "",
            success: false,
        };
    }
};

import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
    return twMerge(clsx(inputs));
}

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
    "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium ring-offset-white transition-colors focus-visible:outline-hidden focus-visible:ring-2 focus-visible:ring-zinc-950 focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0 dark:ring-offset-zinc-950 dark:focus-visible:ring-zinc-300 cursor-pointer",
    {
        variants: {
            variant: {
                default:
                    "bg-zinc-900 text-zinc-50 hover:bg-zinc-900/90 dark:bg-zinc-50 dark:text-zinc-900 dark:hover:bg-zinc-50/90",
                destructive:
                    "bg-red-500 text-zinc-50 hover:bg-red-500/90 dark:bg-red-900 dark:text-zinc-50 dark:hover:bg-red-900/90",
                outline:
                    "border border-zinc-200 bg-white hover:bg-zinc-100 hover:text-zinc-900 dark:border-zinc-800 dark:bg-zinc-950 dark:hover:bg-zinc-800 dark:hover:text-zinc-50",
                secondary:
                    "bg-zinc-100 text-zinc-900 hover:bg-zinc-100/80 dark:bg-zinc-800 dark:text-zinc-50 dark:hover:bg-zinc-800/80",
                ghost: "hover:bg-zinc-100 hover:text-zinc-900 dark:hover:bg-zinc-800 dark:hover:text-zinc-50",
                link: "text-zinc-900 underline-offset-4 hover:underline dark:text-zinc-50",
            },
            size: {
                default: "h-10 px-4 py-2",
                sm: "h-9 rounded-md px-3",
                lg: "h-11 rounded-md px-8",
                icon: "h-10 w-10",
            },
        },
        defaultVariants: {
            variant: "default",
            size: "default",
        },
    }
);

export interface ButtonProps
    extends React.ButtonHTMLAttributes<HTMLButtonElement>,
        VariantProps<typeof buttonVariants> {
    asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
    ({ className, variant, size, asChild = false, ...props }, ref) => {
        const Comp = asChild ? Slot : "button";
        return (
            <Comp
                className={cn(buttonVariants({ variant, size, className }))}
                ref={ref}
                {...props}
            />
        );
    }
);
Button.displayName = "Button";

export { Button, buttonVariants };
