// Tremor BarList [v0.1.1]

import React from "react"

import { cx } from "@/utils/cx"
import { focusRing } from "@/utils/focusRing"

type Bar<T> = T & {
  key?: string
  href?: string
  value: number
  name: string
}

interface BarListProps<T = unknown>
  extends React.HTMLAttributes<HTMLDivElement> {
  data: Bar<T>[]
  valueFormatter?: (value: number) => string
  showAnimation?: boolean
  onValueChange?: (payload: Bar<T>) => void
  sortOrder?: "ascending" | "descending" | "none"
}

function BarListInner<T>(
  {
    data = [],
    valueFormatter = (value) => value.toString(),
    showAnimation = false,
    onValueChange,
    sortOrder = "descending",
    className,
    ...props
  }: BarListProps<T>,
  forwardedRef: React.ForwardedRef<HTMLDivElement>,
) {
  const Component = onValueChange ? "button" : "div"
  const sortedData = React.useMemo(() => {
    if (sortOrder === "none") {
      return data
    }
    return [...data].sort((a, b) => {
      return sortOrder === "ascending" ? a.value - b.value : b.value - a.value
    })
  }, [data, sortOrder])

  const widths = React.useMemo(() => {
    const maxValue = Math.max(...sortedData.map((item) => item.value), 0)
    return sortedData.map((item) =>
      item.value === 0 ? 0 : Math.max((item.value / maxValue) * 100, 2),
    )
  }, [sortedData])

  const rowHeight = "h-8"

  return (
    <div
      ref={forwardedRef}
      className={cx("flex justify-between space-x-6", className)}
      aria-sort={sortOrder}
      tremor-id="tremor-raw"
      {...props}
    >
      <div className="relative w-full space-y-1.5">
        {sortedData.map((item, index) => (
          <Component
            key={item.key ?? item.name}
            onClick={() => {
              onValueChange?.(item)
            }}
            className={cx(
              // base
              "group w-full rounded",
              // focus
              focusRing,
              onValueChange
                ? [
                    "!-m-0 cursor-pointer",
                    // hover
                    "hover:bg-gray-50 hover:dark:bg-gray-900",
                  ]
                : "",
            )}
          >
            <div
              className={cx(
                // base
                "flex items-center rounded transition-all",
                rowHeight,
                // background color
                "bg-blue-200 dark:bg-blue-900",
                onValueChange
                  ? "group-hover:bg-blue-300 group-hover:dark:bg-blue-800"
                  : "",
                // margin and duration
                {
                  "mb-0": index === sortedData.length - 1,
                  "duration-800": showAnimation,
                },
              )}
              style={{ width: `${widths[index]}%` }}
            >
              <div className={cx("absolute left-2 flex max-w-full pr-2")}>
                {item.href ? (
                  <a
                    href={item.href}
                    className={cx(
                      // base
                      "truncate whitespace-nowrap rounded text-sm",
                      // text color
                      "text-gray-900 dark:text-gray-50",
                      // hover
                      "hover:underline hover:underline-offset-2",
                      // focus
                      focusRing,
                    )}
                    target="_blank"
                    rel="noreferrer"
                    onClick={(event) => event.stopPropagation()}
                  >
                    {item.name}
                  </a>
                ) : (
                  <p
                    className={cx(
                      // base
                      "truncate whitespace-nowrap text-sm",
                      // text color
                      "text-gray-900 dark:text-gray-50",
                    )}
                  >
                    {item.name}
                  </p>
                )}
              </div>
            </div>
          </Component>
        ))}
      </div>
      <div>
        {sortedData.map((item, index) => (
          <div
            key={item.key ?? item.name}
            className={cx(
              "flex items-center justify-end",
              rowHeight,
              index === sortedData.length - 1 ? "mb-0" : "mb-1.5",
            )}
          >
            <p
              className={cx(
                // base
                "truncate whitespace-nowrap text-sm leading-none",
                // text color
                "text-gray-900 dark:text-gray-50",
              )}
            >
              {valueFormatter(item.value)}
            </p>
          </div>
        ))}
      </div>
    </div>
  )
}

BarListInner.displayName = "BarList"

const BarList = React.forwardRef(BarListInner) as <T>(
  p: BarListProps<T> & { ref?: React.ForwardedRef<HTMLDivElement> },
) => ReturnType<typeof BarListInner>

export { BarList, type BarListProps }

import React from "react"
import type { Meta, StoryObj } from "@storybook/react"

import { BarList } from "@/components/BarList/BarList"

const meta: Meta<typeof BarList> = {
  title: "visualization/BarList",
  component: BarList,
  render: (args) => <BarList data-testid="barlist" {...args} />,
}

export default meta
type Story = StoryObj<typeof BarList>

const data = [
  { name: "/home", value: 843 },
  { name: "/imprint", value: 46 },
  { name: "/cancellation", value: 3 },
  { name: "/blocks", value: 108 },
  { name: "/documentation", value: 384 },
]

const dataSame = [
  { name: "/home", value: 100 },
  { name: "/imprint", value: 100 },
  { name: "/cancellation", value: 100 },
  { name: "/blocks", value: 100 },
  { name: "/documentation", value: 100 },
]

const dataHref = [
  { name: "/home", value: 843, href: "https://tremor.so" },
  { name: "/imprint", value: 46, href: "https://tremor.so" },
  { name: "/cancellation", value: 3, href: "https://tremor.so" },
  { name: "/blocks", value: 108, href: "https://tremor.so" },
  { name: "/documentation", value: 384, href: "https://tremor.so" },
]

export const Default: Story = {
  args: {
    data: data,
  },
}

export const WithHrefAndValueFormatter: Story = {
  args: {
    data: dataHref,
    valueFormatter: (value) => `${value} Visitors`,
  },
}

export const WithSortOrderAscending: Story = {
  args: {
    data: data,
    sortOrder: "ascending",
  },
}

export const WithSortOrderNone: Story = {
  args: {
    data: data,
    sortOrder: "none",
  },
}

export const WithOnValueChange: Story = {
  args: {
    data: data,
  },
  render: (args) => {
    const [selectedItem, setSelectedItem] = React.useState("")
    return (
      <div className="relative flex flex-col gap-3">
        <BarList
          data-testid="barlist-onvaluechange"
          onValueChange={(item) =>
            setSelectedItem(JSON.stringify(item, null, 2))
          }
          {...args}
        />
        <pre className="w-fit rounded-md bg-gray-100 p-2 font-mono text-sm text-gray-700">
          {selectedItem === "" ? "Click on a bar" : selectedItem}
        </pre>
      </div>
    )
  },
}

export const WithOnValueChangeAndHref: Story = {
  args: {
    data: dataHref,
  },
  render: (args) => {
    const [selectedItem, setSelectedItem] = React.useState("")
    return (
      <div className="relative flex flex-col gap-3">
        <BarList
          data-testid="barlist-onvaluechangehref"
          onValueChange={(item) =>
            setSelectedItem(JSON.stringify(item, null, 2))
          }
          {...args}
        />
        <pre className="w-fit rounded-md bg-gray-100 p-2 font-mono text-sm text-gray-700">
          {selectedItem === "" ? "Click on a bar" : selectedItem}
        </pre>
      </div>
    )
  },
}

export const DataSameWithOnValueChange: Story = {
  args: {
    onValueChange: () => {},
    data: dataSame,
  },
}

export const DataSame: Story = {
  args: {
    data: dataSame,
  },
}

// Tremor cx [v0.0.0]

import clsx, { type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cx(...args: ClassValue[]) {
  return twMerge(clsx(...args))
}

// Tremor focusRing [v0.0.1]

export const focusRing = [
  // base
  "outline outline-offset-2 outline-0 focus-visible:outline-2",
  // outline color
  "outline-blue-500 dark:outline-blue-500",
]
